	vector<int> findSubstring(string s, vector<string>& words) {
		vector<int> result;
		if (words.empty())return result;
		unordered_map<string, int> counts, record;
		map<int, string> indexes;

		int len = words[0].size(), num = words.size();
		int lastIndexOfMin = s.size() - len * num;
		for (size_t i = 0; i < words.size(); i++)
		{
			counts[words[i]]++;
			if (counts[words[i]] == 1) {
				auto v = findall(s, words[i]);
				if (v.empty())return result;
				if (words.size() == 1)return v;
				for (size_t j = 0; j < v.size(); j++)
					indexes[v[j]] = words[i];
				lastIndexOfMin = min(lastIndexOfMin, (int)v[v.size() - 1]);
			}
		}

		for (auto it = indexes.begin();it != indexes.end() && it->first <= lastIndexOfMin; it++)
		{
			record.clear();
			record[it->second]++;
			int formerIdx = it->first;
			for (auto it2 = next(it, 1);it2 != indexes.end() && it2->first <= it->first + len * (num - 1); it2++)
			{
				if (it2->first - formerIdx > len)
					break;
				else if (it2->first - formerIdx == len) {
					record[it2->second]++;
					if (record[it2->second] > counts[it2->second])
						break;
					formerIdx = it2->first;
				}
				if (it2->first == it->first + len * (num - 1)) {
					result.push_back(it->first);
				}
			}
		}

		return result;
	}

	//224 ms	19.8 MB
	vector<int> findSubstring2(string s, vector<string>& words) {
		vector<int> result;
		if (words.empty())return result;
		unordered_map<string, int> counts, record;
		for (string word : words) {
			counts[word]++;
		}
		int len = words[0].size(), num = words.size(), sl = s.size();
		string word;
		for (int i = 0; i < sl - len * num + 1; i++)
		{
			for (int j = 0; j < len * (num - 1) + 1; j += len) {
				word = s.substr(i + j, len);
				if (counts.find(word) != counts.end()) {
					record[word]++;
					if (record[word] > counts[word])
						break;
				}
				else
				{
					break;
				}

				if (j == len * (num - 1))
					result.push_back(i);
			}
		}
		return result;
	}

	//32 ms	14.6 MB
	vector<int> findSubstring3(string s, vector<string>& words) {
		vector<int> result;
		if (words.empty())return result;
		unordered_map<string, int> counts, record;
		for (string word : words) {
			counts[word]++;
		}
		int len = words[0].size(), num = words.size(), sl = s.size();
		
		for (int i = 0; i < len; ++i) {
			int left = i, sum = 0;
			record.clear();
			for (int j = i; j <= sl - len; j+=len) {
				string word = s.substr(j, len);
				if (counts.count(word)) {
					record[word]++;
					sum++;
					while (record[word] > counts[word])
					{
						//remove the most left word
						record[s.substr(left, len)]--;
						left += len;
						sum--;
					}
					if (sum == num)
						result.push_back(left);
				}
				else
				{
					record.clear();
					sum = 0;
					left = j + len;
				}
			}
		}

		return result;
	}
